mapper.sql:
FROM chat_room r JOIN chat_member m using(chaR_num) LEFT OUTER JOIN(SELECT chaR_Num, mem_num, count(*) room_cnt FROM chat_read WHERE mem_num=#{mem_num} GROUP BY chaR_num, mem_num) e USING(chaR_num) LEFT OUTER JOIN (SELECT chaT_num,
REPLACE(REPLACE(REPLACE(chaT_txt,'<','&lt;'),'>','&gt;'),'@{member}@','') chaT_txt,
chaT_time, chaT_status, chaR_num FROM chat_text WHERE chaT_num in(SELECT max(chaT_num) chaT_num FROM chat_text GROUP BY chaR_num)) USING (chaR_num) WHERE m.mem_num=#{mem_num}

