mapper.update:
	  
        UPDATE point_total 
        SET point_total = 
        CASE 
            WHEN #{poi_increase} = 1 THEN point_total - #{poi_use}
            WHEN #{poi_increase} = 2 THEN point_total + #{poi_use}
            ELSE point_total 
        END 
        WHERE mem_num = #{mem_num} 
        AND (#{poi_increase} != 1 OR point_total >= #{poi_use})

mapper.select:
    SELECT * 
    FROM (
        SELECT a.*, rownum rnum 
        FROM (
            SELECT * 
            FROM point 
            JOIN point_total USING(mem_num)
            WHERE mem_num = #{mem_num} 
    		
        ) a
    )

   WHERE rnum >= #{start} AND rnum <= #{end}

SELECT * FROM (SELECT a.*, rownum rnum FROM(
SELECT * FROM point_game

)a)

   WHERE rnum >= #{start} AND rnum <= #{end}

SELECT * FROM (SELECT a.*, rownum rnum FROM(
SELECT * FROM point_game_option WHERE poiG_num=#{poiG_num} 

)a)

   WHERE rnum >= #{start} AND rnum <= #{end}

SELECT * FROM (SELECT a.*, rownum rnum FROM(
SELECT * FROM point_game_betting WHERE poiO_num=#{poiO_num}
)a)

   WHERE rnum >= #{start} AND rnum <= #{end}
