mapper.select:
SELECT *FROM (SELECT a.*, rownum rnum FROM (SELECT teab_num,
REPLACE(REPLACE(teab_title,'<','&lt;'),'>','&gt;') teab_title,
teaB_status, teaB_content, teaB_rdate, teaB_category, filename, tea_num, teaB_hit, mem_num, mem_email, teaR_cnt, mem_nickname FROM team_board JOIN member USING (mem_num) JOIN member_detail USING (mem_num) LEFT OUTER JOIN (SELECT teaB_num, COUNT(*) teaR_cnt FROM team_reply GROUP BY teab_num) USING (teab_num)
)a)
WHERE rnum >= #{start} AND rnum <= #{end} AND tea_num=#{tea_num}
