mapper.select:
SELECT * FROM (SELECT a.*, rownum rnum FROM (SELECT tnr_num,
REPLACE(REPLACE(tnr_title,'<','&lt;'),'>','&gt;') tnr_title,
tnr_hit, filename, tnr_rdate, mem_num, mem_email, mem_nickname, re_cnt, fav_cnt FROM tnrboard JOIN member USING(mem_num) JOIN item USING(item_num) LEFT OUTER JOIN (SELECT COUNT(*) re_cnt, tnr_num FROM tnrboard_reply GROUP BY tnr_num) USING(tnr_num) LEFT OUTER JOIN (SELECT COUNT(*) fav_cnt, tnr_num FROM tnrboard_fav GROUP BY tnr_num) USING(tnr_num) LEFT OUTER JOIN member_detail USING(mem_num)
)a)
WHERE rnum >= #{start} AND rnum <= #{end}

SELECT * FROM (SELECT a.*, rownum rnum FROM (SELECT tnr_num,
REPLACE(REPLACE(tnr_title,'<','&lt;'),'>','&gt;') tnr_title,
tnr_hit, filename, tnr_rdate, mem_num, mem_email, mem_nickname, re_cnt, fav_cnt FROM tnrboard JOIN member USING(mem_num) LEFT OUTER JOIN (SELECT COUNT(*) re_cnt, tnr_num FROM tnrboard_reply GROUP BY tnr_num) USING(tnr_num) LEFT OUTER JOIN (SELECT COUNT(*) fav_cnt, tnr_num FROM tnrboard_fav GROUP BY tnr_num) USING(tnr_num) LEFT OUTER JOIN member_detail USING(mem_num)
AND mem_num = #{mem_num}
)a)
WHERE rnum >= #{start} AND rnum <= #{end}

mapper.update:
UPDATE tnrboard SET
filename = #{filename},
tnr_category = #{tnr_category}, tnr_title = #{tnr_title}, tnr_content = #{tnr_content}, tnr_mdate = SYSDATE WHERE tnr_num = #{tnr_num}
